import React from 'react';

const Cart = (props) => {

    const cart = props.cart.map(item => (<div>
                                            <img src={item.img} alt="Socks"/>
                                            <div>{item.name}</div>
                                            <div>{item.quantity}</div>
                                            <div>
                                                <button onClick={()=>{props.addToCart(item, 1)}}>+</button>{(item.price * item.quantity).toFixed(2)}<button onClick={()=>{props.removeFromCart(item, 'all')}}>-</button>
                                            </div>
                                        </div>))

    return (
        <div>
            {cart}
        </div>
    )
}

export default Cart;
