import React from 'react';
import '../Styles/Product.css'

const Product = (props) => {
    const [inputAmount, setInputAmount] = '0';

    const handleInput = (e) => {
        const input = e.target.value.replace(/\D/,'');
        setInputAmount(input);
    }

    return(
        <div className="Product-Container">
            <img src={props.sockinfo.img} alt="Socks"></img>
            <div>{props.sockinfo.name}</div>
            <div>{props.sockinfo.price}</div>
            <input type={'number'}></input>
            <button onClick={()=>{props.addToCart(props.sockinfo, inputAmount)}} onChange={(e)=>{handleInput(e)}} value={inputAmount}>Add To Cart</button>
        </div>
    )
}

export default Product;
