import React from 'react';
import {Link} from 'react-router-dom';
import '../Styles/Cart.css'

const Cart = (props) => {

    const cartItems = props.cart.map((item, i) => (<li className="Cart-Item-Container" key={i}>
                                            <img src={item.img} alt="Socks"/>
                                            <div>{item.name}</div>
                                            <div>
                                                <button onClick={()=>{props.addToCart(item, 1)}}>+</button>{item.quantity}<button onClick={()=>{props.removeFromCart(item, 'one')}}>-</button></div>
                                            <div>{(item.price * item.quantity).toFixed(2)}</div>
                                            <button onClick={()=>{props.removeFromCart(item, 'all')}}>x</button>
                                        </li>))

    const cart = (
        <div className='Cart-Container'>
        <ul className='Cart-List'>
            {cartItems}
        </ul>
        <div className='Cart-Checkout-Info'>
            <div>Total: {props.totalPrice}</div>
            <button>CheckOut</button>
        </div>
    </div>
    );

    return (
        <>{cartItems[0] ? cart :  
                        <div className='Cart-Container'>
                            <div className='Cart-Empty-Container'>
                                <h2>Cart is empty</h2>
                                <Link to="/Shop">Go to Shop</Link>
                            </div>
                        </div>}</>
    );
}

export default Cart;
