import React from 'react';

const Cart = (props) => {

    const cart = props.cart.map(item => <div><img src={item.img} alt="Socks"></img><div>{item.name}</div><div>{item.quantity}</div><div><button onClick={()=>{props.addToCart(item)}}>+</button>{item.price * item.quantity}<button>-</button></div></div>)

    return (
        <div>
            {cart}
        </div>
    )
}

export default Cart;
