import React from 'react';
import '../Styles/Product.css'

const Product = (props) => {
    const [inputAmount, setInputAmount] = '';

    const handleInput = (e) => {
        const input = e.target.value;
        if (parseInt(input)) {
            setInputAmount(input);
        }
        
    }

    return(
        <div className="Product-Container">
            <img src={props.sockinfo.img} alt="Socks"></img>
            <div>{props.sockinfo.name}</div>
            <div>{props.sockinfo.price}</div>
            <button onClick={()=>{props.addToCart(props.sockinfo)}} onchange={(e)=>{handleInput(e)}} value={inputAmount}>Add To Cart</button>
        </div>
    )
}

export default Product;
