import React from 'react';
import {Link} from 'react-router-dom';

const Cart = (props) => {

    const cartItems = props.cart.map((item, i) => (<li key={i}>
                                            <img src={item.img} alt="Socks"/>
                                            <div>{item.name}</div>
                                            <div>{item.quantity}</div>
                                            <div>
                                                <button onClick={()=>{props.addToCart(item, 1)}}>+</button>{(item.price * item.quantity).toFixed(2)}<button onClick={()=>{props.removeFromCart(item, 'one')}}>-</button>
                                            </div>
                                            <button onClick={()=>{props.removeFromCart(item, 'all')}}>x</button>
                                        </li>))

    const cart = (
        <div className='Cart-Container'>
        <ul>
            {cartItems}
        </ul>
        <div className='Cart-Checkout-Info'>
            <div>Total: {props.totalPrice}</div>
            <button>CheckOut</button>
        </div>
    </div>
    );

    return (
        <>{cartItems ? cart :  ''}</>
    );
}

export default Cart;
