import React, {useState} from 'react';
import './Styles/App.css';
import {Routes, Route, Link} from 'react-router-dom';
import Home from './Components/Home.js';
import Shop from './Components/Shop.js';
import Cart from './Components/Cart.js';

function App() {

  // Array housing objects with data related to the item added and quantity
  const [ cart, setCart ] = useState([]);

  const addToCart = (sockData) => {

    let cartCopy = cart.slice();
    // const itemNotFound = cartCopy[0] ? cartCopy.every(item => {return item.name != sockData.name;}) : true ; //  First it looks if the cartcopy is empty, if the cart is empty it will return true directy, if not it will do a .every on the array and see if the item is in the array, if the item is not in the array it will return true.
    // if (itemNotFound) {
    //   cartCopy = [...cartCopy, sockData]
    // } else {
    //   cartCopy = cartCopy.map(item => item.name == sockData.name ? {name: item.name, price: item.price, quantity: item.quantity + 1, img: item.img} : item)
    // }
    // TODO try reformating this to a for loop where if the loop reaches the cartCopyLength you can then add a new item. If it is better than the spaghetti u have now then chang it to that.

    for (let i = 0; i < cartCopy.length; i++) {
      if (cartCopy[i].name == sockData.name) {
        cartCopy[i].quantity = cartCopy[i].quantity + 1
      } else if (i+1 == cartCopy.length) {
        cartCopy.push(sockData)
      } else {
        continue;
      }
    }

    setCart(cartCopy)
  }

  const removeFromCart = (sockData) => {
    let cartCopy = cart.slice();
    for (let i = 0; i < cartCopy.length ; i++){
      if (cartCopy[i].name == sockData.name) {
        sockData.quantity == 1 ? cartCopy.splice(i, 1) : cartCopy[i].quantity = cartCopy[i].quantity - 1;
      }
    }
    setCart(cartCopy)
    }

  return (
  <div className="App-Container">
    <header className="App-Header">
      <div className="App-Header-Logo">
        <Link to="/">Company Logo</Link>
      </div>
      <ul className="App-Header-List">
        <Link to="/Shop">Shop</Link>
        <Link to="/Cart">Cart</Link>
      </ul>
    </header>
    <Routes>
      <Route path="/" element={<Home />}/>
      <Route path="/shop" element={<Shop addToCart={addToCart} />}/>
      <Route path="/cart" element={<Cart addToCart={addToCart} removeFromCart={removeFromCart} cart={cart} />}/>
    </Routes>
  </div>
  );
}

export default App;
