import React, {useState} from 'react';
import '../Styles/Product.css'

const Product = (props) => {
    const [inputAmount, setInputAmount] = useState(0);

    const handleInput = (e) => {
        if (e.target.value == '') {
            setInputAmount(0);
        }
        const input = e.target.value.replace(/[\D""]/,0);
        setInputAmount(parseInt(input));
    }

    const handleTuning = (amount) => {
        if (inputAmount === 0 && amount === -1) {
            return;
        }
        setInputAmount(inputAmount + amount)
    }

    return(
        <div className="Product-Container">
            <img src={props.sockinfo.img} alt="Socks"></img>
            <div>{props.sockinfo.name}</div>
            <div>{props.sockinfo.price}</div>
            <input onChange={(e)=>{handleInput(e)}} type="text" maxLength={2} value={inputAmount}></input>
            <button onClick={()=>handleTuning(1)}>+</button>
            <button onClick={()=>{handleTuning(-1)}}>-</button>
            <button onClick={()=>{props.addToCart(props.sockinfo, inputAmount)}}>Add To Cart</button>
        </div>
    )
}

// TODO cart add input

export default Product;
