import React, {useState} from 'react';
import './Styles/App.css';
import {Routes, Route} from 'react-router-dom';
import Home from './Components/Home.js';
import Shop from './Components/Shop.js';
import Cart from './Components/Cart.js';

function App() {

  // Array housing objects with data related to the item added and quantity
  const [ cart, setCart ] = useState([]);

  const addToCart = (sockData) => {
    // TODO Implement logic to check if item is already in the cart and deal with it appropriatley.
    let cartCopy = cart.slice;
    const isBelowThreshold = (currentValue) => currentValue < 40;
    const itemNotFound = cartCopy.every(item => {return item.name != sockData.name;});
    if (itemNotFound) {
      cartCopy = cartCopy.map(item => item.name == sockData.name && item.quantity + 1)
    } else {
      cartCopy = [...cartCopy, {name: sockData.name, price: sockData.price, quantity: 1}]
    }
    setCart(cartCopy)
  }

  return (
  <div className='App-Container'>
    <header className='App-Header'>
      <div className='App-Header-Logo'>
        <a href='/'>Company Logo</a>
      </div>
      <ul className='App-Header-List'>
        <a href='/Shop'>Shop</a>
        <a href='/Cart'>Cart</a>
      </ul>
    </header>
    <Routes>
      <Route path="/" element={<Home />}/>
      <Route path="/shop" element={<Shop addToCart={addToCart}/>}/>
      <Route path="/cart" element={<Cart />}/>
    </Routes>
  </div>
  );
}

export default App;
